class DataApi {
  constructor(rawData){
    this.rawData = rawData;
  }
  
  mapIntoObject(arr){
    return arr.reduce((accumelator, currente) =>{
      accumelator[currente.id] = currente;
      return accumelator;
    },{});
  }

  getArticles(){
    return this.mapIntoObject(this.rawData.articles);
  }
  getAuthors(){
    return this.mapIntoObject(this.rawData.authors);
  }
}
export default DataApi;